import React from "react";
// material-ui components
import InputAdornment from "@material-ui/core/InputAdornment";
import Icon from "@material-ui/core/Icon";
// @material-ui/icons
import { makeStyles } from "@material-ui/core/styles";
// core components
import Button from "components/CustomButtons/Button.js";
import CardBody from "components/Card/CardBody.js";
import CardHeader from "components/Card/CardHeader.js";
import CustomInput from "components/CustomInput/CustomInput.js";
import CardFooter from "components/Card/CardFooter";
import Email from "@material-ui/icons/Email";
import People from "@material-ui/icons/People";
import {Link} from "react-router-dom";

import GridContainer from "../../components/Grid/GridContainer";
import GridItem from "../../components/Grid/GridItem";
import styles from "assets/jss/material-kit-react/views/loginPage.js";

const useStyles = makeStyles(styles);
//const classes = useStyles();

export default class SignupButton extends React.Component {

  constructor(props) {
    super(props);
    this.state = {

      email: "",
      password: "",
      firstName: "",
    };

    this.handleEmailChange = this.handleEmailChange.bind(this);

    this.handlePasswordChange = this.handlePasswordChange.bind(this);
    this.handleFirstNameChange = this.handleFirstNameChange.bind(this);

    this.handleSubmit = this.handleSubmit.bind(this);
  }
  render() {
    return (
      <form className={useStyles.form} >
<CardHeader color="primary" className={useStyles.cardHeader}>
  <h4>Login</h4>
  <div className={useStyles.socialLine}>
    <Button
      justIcon
      href="#pablo"
      target="_blank"
      color="transparent"
      onClick={e => e.preventDefault()}
    >
      <i className={"fab fa-twitter"} />
    </Button>
    <Button
      justIcon
      href="#pablo"
      target="_blank"
      color="transparent"
      onClick={e => e.preventDefault()}
    >
      <i className={"fab fa-facebook"} />
    </Button>
    <Button
      justIcon
      href="#pablo"
      target="_blank"
      color="transparent"
      onClick={e => e.preventDefault()}
    >
      <i className={"fab fa-google-plus-g"} />
    </Button>
  </div>
</CardHeader>
<p className={useStyles.divider}>Or Be Classical</p>
<CardBody>
  <CustomInput
    labelText="First Name..."
    id="first"
    formControlProps={{
      fullWidth: true
    }}
    inputProps={{
      type: "text",
      endAdornment: (
        <InputAdornment position="end">
          <People className={useStyles.inputIconsColor} />
        </InputAdornment>
      )
    }}
  />
  <CustomInput
    labelText="Email..."
    id="email"
    formControlProps={{
      fullWidth: true
    }}
    inputProps={{
      type: "email",
      endAdornment: (
        <InputAdornment position="end">
          <Email className={useStyles.inputIconsColor} />
        </InputAdornment>
      )
    }}
  />
  <CustomInput
    labelText="Password"
    id="pass"
    formControlProps={{
      fullWidth: true
    }}
    inputProps={{
      type: "password",
      endAdornment: (
        <InputAdornment position="end">
          <Icon className={useStyles.inputIconsColor}>
            lock_outline
          </Icon>
        </InputAdornment>
      ),
      autoComplete: "off"
    }}
  />
</CardBody>
<CardFooter className={useStyles.cardFooter}>
  <Button simple color="primary" size="lg" >
    Get started
  </Button>
</CardFooter>
</form>
    );
  }
}
